mapper.select:
SELECT count(like_id)
FROM dc_like
WHERE like_liker_id = #{userId};

SELECT *
from dc_like
WHERE like_liker_id = #{userId}
  AND like_post_id = #{postId};

mapper.delete:
DELETE
FROM dc_like
WHERE like_liker_id = #{likeLikerId}
  AND like_post_id = #{likePostId};

